project.script:
var ver = project.getProperty("VERSION");
if (ver == null) {
	var prjs = project.getProperty("prj.deps");
	if (prjs == null) {
		prjs = ".";
	}
	else {
		prjs += ";.";
	}
	prjs = prjs.split(';');

	function run(cmd) {
		var is = null;
		try {
			var p = java.lang.Runtime.getRuntime().exec(cmd);
			is = p.getInputStream();
			var br = new java.io.BufferedReader(new java.io.InputStreamReader(is));
			var o = "";
			var s;
			while ((s = br.readLine()) != null) {
				o += s.trim();
			}
			return o;
		}
		catch (e) {
			java.lang.System.err.println(e);
			return "";
		}
		finally {
			if (is != null) {
				is.close();
			}
		}
	}

	var rev = "";
	for (var i = 0; i < prjs.length; i++) {
		var p = prjs[i];
		var r = "0";
		var git = new java.io.File(p + "/.git");
		if (git.exists()) {
			var s = run("git --git-dir=" + git.getPath() + " rev-list --all --count");
			if (s != "") {
				r = s;
			}
		}
		else {
			var svn = new java.io.File(p + "/.svn");
			if (svn.exists()) {
				var si = run("svn info --xml " + p);
				if (si != "") {
					var b = si.indexOf("revision=\"") + 10;
					var e = si.indexOf("\"", b);
					r = si.substring(b, e);
				}
			}
		}
		if (rev != "") {
			rev += "-";
		}
		rev += r;
	}
	project.setProperty("REVISION", rev);
	project.setProperty("VERSION", project.getProperty("prj.version") + "." + rev);
}
