project.script:
var rev = project.getProperty("REVISION");
if (rev == null) {
	var prjs = project.getProperty("prj.deps");
	if (prjs == null) {
		prjs = ".";
	}
	else {
		prjs += ";.";
	}
	prjs = prjs.toString().split(';');

	function run(cmd) {
		var is = null;
		try {
			var p = java.lang.Runtime.getRuntime().exec(cmd);
			is = p.getInputStream();
			var br = new java.io.BufferedReader(new java.io.InputStreamReader(is));
			var o = "";
			var s;
			while ((s = br.readLine()) != null) {
				o += s.trim();
			}
			return o;
		}
		catch (e) {
			java.lang.System.err.println(e);
			return "";
		}
		finally {
			if (is != null) {
				is.close();
			}
		}
	}

	rev = "";
	var cev = "";
	for (var i = 0; i < prjs.length; i++) {
		var p = prjs[i];
		var r = "";
		var svn = new java.io.File(p + "/.svn");
		if (svn.exists()) {
			var si = run("svn info --xml " + p);
			if (si != "") {
				var b = si.indexOf("revision=\"") + 10;
				var e = si.indexOf("\"", b);
				r = si.substring(b, e);
			}
		}
		else {
			var git = new java.io.File((new java.io.File(p)).getAbsolutePath() + "/.git");
			while (git != null && !git.exists()) {
				git = git.getParentFile().getParentFile();
				if (git != null) {
					git = new java.io.File(git, ".git");
				}
			}
			if (git != null) {
				r = run("git --git-dir=" + git.getPath() + " rev-list --count HEAD");

				var c = run("git --git-dir=" + git.getPath() + " rev-parse --short HEAD");
				if (c != "") {
					if (cev != "") {
						cev += "-";
					}
					cev += c;
				}
			}
		}
		if (r != "") {
			if (rev != "") {
				rev += "-";
			}
			rev += r;
		}
	}

	if (rev == "") {
		rev = "0";
	}
	if (cev != "") {
		rev += "." + cev;
	}
	project.setProperty("REVISION", rev);
}

var pver = project.getProperty("PVERSION");
if (pver == null) {
	pver = project.getProperty("prj.version");
}
else {
	project.setProperty("prj.version", pver);
}

var ver = project.getProperty("VERSION");
if (ver == null) {
	ver = pver + "." + rev;
	project.setProperty("VERSION", ver);
}

var gver = project.getProperty("GVERSION");
if (gver == null) {
	gver = java.util.regex.Pattern.compile("\\.").matcher(ver.toString()).replaceAll("-");
	project.setProperty("GVERSION", gver);
}
